// Import the additional functions library
local f = import "functions";

local geminiOutput = std.parseJson(std.extVar('`Task_1_connectorOutputPayload`')[0].ResponseBody);

local filterContent(item) = std.objectHas(item.candidates[0], "content");
local getText(item) = item.candidates[0].content.parts[0].text;

// joints all texts, removes newlines
local finalText = std.strReplace(std.join("",std.filterMap(filterContent, getText, geminiOutput)),'\n','');


{
 output: finalText,
}

